      out.write("            if (!/^\\d{6}$/.test(birthValue)) {\r\n");
      out.write("                flagInputArrAll = false;\r\n");
      out.write("                alert(\"생년월일은 숫자 6자리로만 입력해주세요.\");\r\n");
