      out.write("                break;\r\n");
      out.write("            }\r\n");
      out.write("        }\r\n");
      out.write("        // 전화번호가 숫자로만 11자리인지 확인\r\n");
      out.write("        if (i === 2) {\r\n");
      out.write("            let telValue = arrEssential[i].val();\r\n");
