      out.write("function convertToDate(userBirthday) {\r\n");
      out.write("    var userBirthday = document.memberUpdateFrm.userBirthday.value;\r\n");
      out.write("    \r\n");
      out.write("    // 6자리 숫자로부터 연, 월, 일을 추출\r\n");
      out.write("    var year = userBirthday.substring(0, 2);\r\n");
      out.write("    var month = userBirthday.substring(2, 4);\r\n");
      out.write("    var day = userBirthday.substring(4, 6);\r\n");
      out.write("\r\n");
      out.write("    // 현재 연도를 기준으로 2000년 이후와 2000년 이전의 구분\r\n");
      out.write("    var currentYear = new Date().getFullYear();\r\n");
      out.write("    var century = currentYear < 2000 ? \"19\" : \"20\";\r\n");
      out.write("\r\n");
      out.write("    // YY 형식의 연도를 YYYY 형식으로 변환\r\n");
      out.write("    year = century + year;\r\n");
      out.write("\r\n");
      out.write("    // 월과 일이 한 자리 숫자인 경우 앞에 0을 붙여줌\r\n");
      out.write("    if (month.charAt(0) === \"0\") {\r\n");
      out.write("        month = month.charAt(1);\r\n");
      out.write("    }\r\n");
      out.write("    if (day.charAt(0) === \"0\") {\r\n");
      out.write("        day = day.charAt(1);\r\n");
      out.write("    }\r\n");
      out.write("\r\n");
      out.write("    // 월에 1을 더하여 올바른 월로 변환\r\n");
      out.write("    month = parseInt(month) + 1;\r\n");
      out.write("\r\n");
      out.write("    // YYYY-MM-DD 형식으로 반환\r\n");
      out.write("    return year + \"-\" + month + \"-\" + day;\r\n");
      out.write("}\r\n");
